import React from "react";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import Home from "./pages/Home";
import Detail from "./pages/Detail";
import Histo from "./pages/Histo";
import Fav from "./pages/Fav";
import Login from "./components/Login";
import Register from "./components/Register";
import Content from "./BackOffice/components_template/Content";
import Acceuil from "./BackOffice/pages_admin/Acceuil";
import { Navigate } from "react-router-dom";
import  { useState, useEffect } from "react";

function App() {
    const [userRole, setUserRole] = useState(null);

    useEffect(() => {
        const token = localStorage.getItem("token");
        if (token) {
            // Analysez le token JWT pour obtenir les informations sur l'utilisateur, y compris le rôle
            const decodedToken = parseJwt(token);
            const role = decodedToken ? decodedToken.role : null;
            setUserRole(role);
        }
    }, []);

    // Fonction pour décoder un token JWT et récupérer ses informations
    const parseJwt = (token) => {
        try {
            return JSON.parse(atob(token.split('.')[1]));
        } catch (e) {
            return null;
        }
    };
    return (
        <Router>
            <Routes>
                <Route path="/" element={<Navigate to="/login" />} />
                <Route path="/details" element={<Detail />} />
                <Route path="/home" element={userRole === "USER" ? <Home /> : <Navigate to="/home" />} />
                <Route path="/login" element={<Login />} />
                <Route path="/register" element={<Register />} />
                <Route path="/favoris" element={<Fav />} />
                <Route path="/historiques" element={<Histo />} />
                <Route path="/acceuil" element={userRole === "ADMIN" ? <Acceuil contenu={Content} /> : <Navigate to="/acceuil" />} />
            </Routes>
        </Router>
    );
}

export default App;
