import { Link } from "react-router-dom";
import { useState, useEffect } from "react";

function Header() {
    const [isLoggedin, setIsLoggedin] = useState(false);

    useEffect(() => {
        const token = localStorage.getItem("token-info");
        if (token) {
            setIsLoggedin(true);
        }
    }, []);

    const logout = () => {
        if (token !== null) {
            window.localStorage.setItem("auth_token", token);
          } else {
            window.localStorage.removeItem("auth_token");
          }
    };
    

    return (
        <nav className="navbar navbar-expand-lg bg-body-tertiary">
            <div className="container-fluid">
                <Link className="navbar-brand" to="/">Voiture</Link>
                <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
                    <span className="navbar-toggler-icon"></span>
                </button>
                <div className="collapse navbar-collapse" id="navbarSupportedContent">
                    <ul className="navbar-nav me-auto mb-2 mb-lg-0">
                        <li className="nav-item">
                            <Link className="nav-link active" aria-current="page" to="/">Acceuil</Link>
                        </li>
                        <li className="nav-item">
                            <Link className="nav-link active" aria-current="page" to="/">Annonces auto</Link>
                        </li>
                        <li className="nav-item">
                            <Link className="nav-link active" aria-current="page" to="/">Vendre</Link>
                        </li>
                        <li className="nav-item">
                            <Link className="nav-link active" aria-current="page" to="/favoris">Mes favoris</Link>
                        </li>
                        <li className="nav-item">
                            <Link className="nav-link active" aria-current="page" to="/historiques">Mon historique</Link>
                        </li>
                    </ul>
                    {isLoggedin ? (
                        <button type="button" className="btn btn-link nav-link active" onClick={logout}>Logout</button>
                    ) : (
                        <Link className="nav-link active" aria-current="page" to="/login">Login</Link>
                    )}
                    <Link className="nav-link active" aria-current="page" to="/">Messages <span style={{color: "red"}}>1</span></Link>
                </div>
            </div>
        </nav>
    );
}

export default Header;
