import React, {useEffect, useState} from "react";
import axios from "axios";
import {Link} from "react-router-dom";

export default function Marque(){
    const [marques, setMarques] = useState([]);

    useEffect(() => {
        const token = localStorage.getItem("token");
        if (token) {
            axios.get("https://voiture-backend-production.up.railway.app/liste_marques", {
                headers: {
                    Authorization: `Bearer ${token}`
                }
            })
                .then(response => {
                    setMarque(response.data);
                })
                .catch(error => {
                    console.error('Error fetching marques:', error);
                });
        }
    }, []);


    return (
        <>
            <>
                <Link to="/ajout_categorie" className="btn btn-primary">
                    ajout categorie
                </Link>
            </>

            <>
                <table className="table">
                    <thead>
                    <tr>
                        <th scope="col">#</th>
                        <th scope="col">Categorie</th>
                        <th scope="col"></th>
                        <th scope="col"></th>
                    </tr>
                    </thead>
                    <tbody>
                    {marque.map(marques => (
                        <tr key={marques.idmarque}>
                            <th scope="row">{marques.idmarque}</th>
                            <td>{marques.Marque}</td>
                            <td></td>
                            <td></td>
                        </tr>
                    ))}
                    </tbody>
                </table>
            </>
        </>
    );
}
